sys_script_client.script:
function onLoad() {
   //Type appropriate comment here, and begin script below
	g_form.clearValue('u_status');    
	if (g_form.getValue('u_q4mnfinishflag') ) {
		 g_form.setValue('u_status',"４Ｑ完了");
	}
	else if (g_form.getValue('u_q3mnfinishflag') ) {               
		 g_form.setValue('u_status',"３Ｑ完了"); 
	}
	else if (g_form.getValue('u_q2mnfinishflag')　) { 
		 g_form.setValue('u_status',"２Ｑ完了");
	}
	else if (g_form.getValue('u_q1mnfinishflag') ) {
		 g_form.setValue('u_status',"１Ｑ完了");
    }
	else {g_form.setValue('u_status',"完了ＳＴＳなし");
	}
}
